import React, { useState } from 'react';
import "react-datetime/css/react-datetime.css";
import Datetime from 'react-datetime';

function Appointment(props) {

    const [data, setData] = useState({
        patientName: '',
        reason: '',
        gender: '',
        prefferedDate: '',
        dateOfBirth: '',
        phoneNumber: ''
    })

  const onChangeFirstName = () => {


    };

  const onChangeReason = () => {

}

  const onChangeGender = (e) => {
      setData({
          ...data,
          gender: e.target.value
      });
  }

  const onChangePrefferedDate = (date) => {
      console.log("Date: " + date.toDate())
      setData({
          ...data,
          prefferedDate: date.toDate()
      })
      console.log(data.prefferedDate)
  }

  const onChangeDateOfBirth = (date) => {
      setData({
          ...data,
          dateOfBirth: date.toDate()
      })
  }

  const onChangePhoneNumber = (e) => {
      setData({
          ...data,
          phoneNumber: e.target.value
      })
  }

  const handleSubmit = () => {

  }

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column'}}>
        <label>
            Date:
            <Datetime value={data.prefferedDate} onChange={ onChangePrefferedDate }/>
        </label><br />
        <label>
            Reason:
            <input type="text" value={data.reason} onChange={ onChangeReason } style={{ flex: '10', padding: '5px' }}/>
        </label><br />
        <label>
            Patient Name:
            <input type="text" value={data.firstName} onChange={ onChangeFirstName } style={{ flex: '10', padding: '5px' }}/>
        </label><br />
        <label>
            Gender:
            <select value={data.gender} onChange={ onChangeGender }>
                <option value="male">M</option>
                <option value="female">F</option>
            </select>
        </label><br />
        <label>
            Date of Birth:
            <Datetime value={data.dateOfBirth} timeFormat={false} onChange={ onChangeDateOfBirth }/>
        </label><br />
        <label>
            <div>
                Phone Number:
                <input type="text" value={data.phoneNumber} onChange={ onChangePhoneNumber } style={{ flex: '10', padding: '5px' }}/>
            </div>
        </label><br />
    </form>

  );
}

export default Appointment;
