import React, { useState, useEffect } from 'react';
import Doctors from './Doctors';

function Home(props) {

  const [doctors, setDoctors] = useState(
    [
    ]
  )

  const [filter, setFilter] = useState('');


  //Set Appointment
  const setAppointment = (id) => {
    props.history.push({
        pathname: '/appointment',
        state: id});
  }

  const addDoctor = () => {
      console.log("Add Doctor");
      props.history.push('/adddoctor');
  }

  const filterDoctors = (e) => {
    console.log("Filter Before: " + {filter});
    setFilter(e.target.value, findRoutes(e.target.value));
    console.log("Filter After: " + filter);
    if(!filter) {
      console.log("Filter: " + filter);
      fetchData()
    }
    else {
      const lolo = doctors.filter(doctor => doctor.provider_full_name.includes(e.target.value));
      setDoctors(lolo);
    }
  }

  //Call
  useEffect(() => {
    fetchData()
  }, []);
  //End Call

  const fetchData = () => {
    fetch('/doctors').then(res => res.json()).then(data => {
      setDoctors(data);
    });
  }

  return (
      <div className="App">
        <input type="text" value={filter} onChange={filterDoctors}/>
        <Doctors doctors={doctors} setAppointment={setAppointment}/>
        <button style={buttonStyle} onClick={ addDoctor }>Add Doctor</button>
      </div>
  );
}

const buttonStyle = {
    background: '#FFF',
    color: '#000',
    border: '1px solid',
    padding: '5px 9px',
    borderRadius: '10',
    display: 'flex',
    justifyContent: "center",
    margin: '0 auto',
    marginTop: '20px'  
  }

export default Home;
