import React, { useState } from 'react';
import "react-datetime/css/react-datetime.css";
import Datetime from 'react-datetime';

function Appointment(props) {

    const [data, setData] = useState({
        patientName: '',
        reason: '',
        gender: '',
        prefferedDate: '',
        dateOfBirth: '',
        phoneNumber: ''
    })

  const onChangeName = () => {
        setData({
            ...data,
            patientName: e.target.value
        })
    };

  const onChangeReason = (e) => {
      setData({
          ...data,
          reason: e.target.value
      })
    }

  const onChangeGender = (e) => {
      setData({
          ...data,
          gender: e.target.value
      });
  }

  const onChangePrefferedDate = (date) => {
      console.log("Date: " + date.toDate())
      setData({
          ...data,
          prefferedDate: date.toDate()
      })
      console.log(data.prefferedDate)
  }

  const onChangeDateOfBirth = (date) => {
      setData({
          ...data,
          dateOfBirth: date.toDate()
      })
  }

  const onChangePhoneNumber = (e) => {
      setData({
          ...data,
          phoneNumber: e.target.value
      })
  }

  const handleSubmit = () => {

  }

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column'}}>
        <label style={label}>
            Date:
            <Datetime value={data.prefferedDate} onChange={ onChangePrefferedDate } style={{padding: '5px' }}/>
        </label>
        <label style={label}>
            Reason:
            <div>
                <input type="text" value={data.reason} onChange={ onChangeReason } style={{padding: '5px', width: '400px' }}/>
            </div>
        </label>
        <label style={label}>
            Patient Name:
            <div>
                <input type="text" value={data.firstName} onChange={ onChangeName } style={{padding: '5px' }}/>
            </div>
        </label>
        <label style={label}>
            Gender:
            <div>
                <select value={data.gender} onChange={ onChangeGender } style={{ padding: '5px' }}>
                    <option value="male">M</option>
                    <option value="female">F</option>
                </select>
            </div>
        </label>
        <label style={label}>
            Date of Birth:
            <Datetime value={data.dateOfBirth} timeFormat={false} onChange={ onChangeDateOfBirth } style={{ padding: '5px' }}/>
        </label>
        <label style={label}>
            Phone Number:
            <div>
            <input type="text" value={data.phoneNumber} onChange={ onChangePhoneNumber } style={{ padding: '5px'}}/>
            </div>
        </label><br />
    </form>

  );
}

const label = {
    margin: '5px'
}

export default Appointment;
